const SidebarIcons = ({
  iconName,
  className,
  fillColor,
  width,
  height,
}: {
  iconName?: string;
  className?: string;
  fillColor?: string;
  width?: number;
  height?: number;
}) => {
  switch (iconName) {
    case 'home':
      return (
        <HomeIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'home-filled':
      return (
        <HomeFillIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'explore':
      return (
        <ExploreIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'explore-filled':
      return (
        <ExploreFillIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'course-lesson':
      return (
        <CourseLessonIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'course-lesson-filled':
      return (
        <CourseLessonFillIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'instructor':
      return (
        <InstructorIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'instructor-filled':
      return (
        <InstructorFillIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'video':
      return (
        <VideoIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
    case 'video-filled':
      return (
        <VideoFillIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );

    default:
      return (
        <HomeIcon
          className={className}
          width={width}
          height={height}
          fillColor={fillColor}
        />
      );
  }
};

export default SidebarIcons;
